<?php/** @see Zend_Service_ReCaptcha_Response */class Google_Service_ReCaptcha extends Zend_Service_ReCaptcha{        public function getHtml($name = null) {                if ($this->_publicKey === null) {            /** @see Zend_Service_ReCaptcha_Exception */            require_once 'Zend/Service/ReCaptcha/Exception.php';            throw new Zend_Service_ReCaptcha_Exception('Отсутствует публичный ключ');        }        $errorPart = '';        if (!empty($this->_params['error'])) {            $errorPart = '&error=' . urlencode($this->_params['error']);        }        $return = <<<HTML<div class="g-recaptcha" data-sitekey="{$this->_publicKey}{$errorPart}"></div>HTML;        return $return;         }        public function verify($challengeField, $responseField)    {        $response = $this->_post($challengeField, 'g-recaptcha-response');        return new Zend_Service_ReCaptcha_Response(null, null, $response);    }}